mapper.update:
  UPDATE
    goods_limit_info
  SET
    is_deleted = 1,
    update_time = NOW()
  WHERE
    pid = #{pid}
  AND limit_id=#{limitId}
/*GoodsLimitInfoDao.deleteGoodsLimitByLimitId*/

