mapper.select:
select

from
goods_limit_info
where

    (pid = #{item.pid}
    and goods_id = #{item.goodsId}
    and limit_id = #{item.limitId}
    and is_deleted = 0)

/*GoodsLimitInfoDao.queryGoodsLimitInfoList*/

mapper.update:
UPDATE
    goods_limit_info
set limit_num=#{limitNum},
    limit_type=#{limitType},
    update_time=NOW()
WHERE
    pid = #{pid}
    AND   limit_id=#{limitId}
    AND   goods_id=#{goodsId}
    AND   limit_level=#{limitLevel}
    AND   is_deleted = 0
/*GoodsLimitInfoDao.updateGoodsLimitInfoEntity*/

update
    goods_limit_info
set
    is_deleted = 1,
    update_time = NOW()
where
    pid = #{pid}
    and   limit_id=#{limitId}
/*GoodsLimitInfoDao.deleteLimit*/

update
goods_limit_info
set
is_deleted = 1,
update_time = NOW()
where
pid = #{pid}
and limit_id=#{limitId}
AND
goods_id IN

    #{id}

/*GoodsLimitInfoDao.deleteGoodsLimit*/

mapper.select:
select goodslimitinfo.pid pid, goodslimitinfo.store_id store_id, goodslimitinfo.limit_id limit_id, goodslimitinfo.limit_type limit_type, goodslimitinfo.goods_id goods_id, goodslimitinfo.limit_num limit_num, goodslimitinfo.limit_level limitLevel, goodslimitinfo.sold_num sold_num, goodslimitinfo.version version from goods_limit_info as goodslimitinfo, limit_info as limitinfo where limitinfo.limit_id=goodslimitinfo.limit_id AND (
( goodslimitinfo.pid = #{item.pid} and goodslimitinfo.goods_id = #{item.goodsId} and limitinfo.biz_id = #{item.bizId} and limitinfo.biz_type = #{item.bizType} and goodslimitinfo.is_deleted = 0 and limitinfo.is_deleted = 0 )
) /*GoodsLimitInfoDao.queryOrderGoodsLimitInfoList*/

select
goodslimitinfo.pid pid,
goodslimitinfo.store_id store_id,
goodslimitinfo.limit_id limit_id,
goodslimitinfo.limit_type limit_type,
goodslimitinfo.goods_id goods_id,
goodslimitinfo.limit_num limit_num,
goodslimitinfo.limit_level limitLevel,
goodslimitinfo.sold_num sold_num,
goodslimitinfo.version version
from
goods_limit_info as goodslimitinfo,
limit_info as limitinfo
where
limitinfo.limit_id=goodslimitinfo.limit_id
AND (

    (
    limitinfo.pid = #{item.pid}
    and goodslimitinfo.goods_id = #{item.goodsId}
    and limitinfo.biz_id = #{item.bizId}
    and limitinfo.biz_type = #{item.bizType}
    and


            goodslimitinfo.is_deleted = #{item.checkDeleteActivityGoods}


            goodslimitinfo.is_deleted = 0


    and


            limitinfo.is_deleted = #{item.checkDeleteActivityGoods}


            limitinfo.is_deleted = 0


    )

)
/*GoodsLimitInfoDao.queryGoodsLimitNumList*/

mapper.update:
update
goods_limit_info
set
is_deleted = 0,
update_time = NOW()
where
pid = #{pid}
and limit_id=#{limitId}
AND
goods_id IN

    #{id}

/*GoodsLimitInfoDao.reverseGoodsLimit*/

mapper.select:
select

from
goods_limit_info
where
pid = #{pid}
and limit_id = #{limitId}
and is_deleted = 0
/*GoodsLimitInfoDao.queryAllGoodsLimitInfoList*/
