mapper.select:
select

from
limit_info
where
pid = #{pid}
and biz_id = #{bizId}
and biz_type = #{bizType}
/*LimitInfoDao.selectByLimitParam*/

select

from
limit_info
where

    (pid = #{item.pid}
    and biz_id = #{item.bizId}
    and biz_type = #{item.bizType}
    and is_deleted = 0)

/*LimitInfoDao.queryLimitInfoList*/

mapper.update:
update
    limit_info
set
    is_deleted = 1,
    update_time = NOW(),
    version = version+1
where
    limit_id=#{limitId}
    and version=#{version}
/*LimitInfoDao.delete*/

