mapper.update:
update
    sku_limit_info
set
    is_deleted = 1,
    update_time = NOW()
where
    pid = #{pid}
    and   limit_id=#{limitId}
/*SkuLimitInfoDao.deleteLimit*/

update
sku_limit_info
set
is_deleted = 1,
update_time = NOW()
where
pid = #{pid}
and limit_id=#{limitId}
AND
goods_id IN

    #{id}

/*SkuLimitInfoDao.deleteSkuLimit*/

update
    sku_limit_info
SET
  sold_num = sold_num +#{soldNum},
  update_time = NOW()
WHERE
    pid = #{pid}
AND limit_id = #{limitId}
AND goods_id = #{goodsId}
AND sku_id = #{skuId}
AND limit_num >= sold_num + #{soldNum}
/*SkuLimitInfoDao.update*/

mapper.select:
select skulimtinfo.pid pid, skulimtinfo.store_id store_id, skulimtinfo.limit_id limit_id, skulimtinfo.limit_type limit_type, skulimtinfo.goods_id goods_id, skulimtinfo.sku_id sku_id, skulimtinfo.limit_num limit_num, skulimtinfo.sold_num sold_num, skulimtinfo.version version from sku_limit_info as skulimtinfo, limit_info as limitinfo where limitinfo.limit_id=skulimtinfo.limit_id AND (
( skulimtinfo.pid = #{item.pid} and skulimtinfo.goods_id = #{item.goodsId} and skulimtinfo.sku_id =#{item.skuId} and limitinfo.biz_id = #{item.bizId} and limitinfo.biz_type = #{item.bizType} and skulimtinfo.is_deleted = 0 and limitinfo.is_deleted = 0 )
) /*SkuLimitInfoDao.queryOrderSkuLimitInfoList*/

select pid, store_id, limit_id, limit_type, goods_id, sku_id, limit_num, sold_num, version from sku_limit_info where
( pid = #{item.pid} and goods_id = #{item.goodsId}
and sku_id =#{item.skuId}
and is_deleted = 0 and limit_id = #{item.limitId} )
/*SkuLimitInfoDao.querySkuLimitList*/

mapper.update:
UPDATE
sku_limit_info
SET
sold_num = sold_num - #{soldNum},
update_time = NOW()
WHERE
pid = #{pid}
AND limit_id = #{limitId}
AND goods_id = #{goodsId}
AND sku_id =#{skuId}
AND sold_num >= #{soldNum}
/*SkuLimitInfoDao.deductSkuLimit*/

mapper.select:
select
skulimitinfo.pid pid,
skulimitinfo.store_id store_id,
skulimitinfo.limit_id limit_id,
skulimitinfo.limit_type limit_type,
skulimitinfo.goods_id goods_id,
skulimitinfo.sku_id sku_id,
skulimitinfo.limit_num limit_num,
skulimitinfo.sold_num sold_num,
skulimitinfo.version version
from
sku_limit_info as skulimitinfo,
limit_info as limitinfo
where
limitinfo.limit_id=skulimitinfo.limit_id
AND (

    (
    limitinfo.pid = #{item.pid}
    and skulimitinfo.goods_id = #{item.goodsId}
    and limitinfo.biz_id = #{item.bizId}
    and limitinfo.biz_type = #{item.bizType}
    and


            skulimitinfo.is_deleted = #{item.checkDeleteActivityGoods}


            skulimitinfo.is_deleted = 0


    and


            limitinfo.is_deleted = #{item.checkDeleteActivityGoods}


            limitinfo.is_deleted = 0


    )

)
/*SkuLimitInfoDao.queryGoodsSkuLimitList*/

select pid, store_id, limit_id, limit_type, goods_id, sku_id, limit_num, sold_num, version from sku_limit_info where pid = #{pid} and goods_id in
#{id}
and limit_id = #{limitId} and is_deleted = 0 /*SkuLimitInfoDao.queryOldSkuLimitList*/

mapper.update:
update
sku_limit_info
SET
limit_num = limit_num + #{limitNum},
update_time = NOW()
WHERE
pid = #{pid}
AND limit_id = #{limitId}
AND goods_id = #{goodsId}
AND sku_id = #{skuId}
AND limit_num + #{limitNum} >=0
