mapper.select:
select

from
user_goods_limit
where

    (pid = #{item.pid}
    and goods_id = #{item.goodsId}
    and limit_id = #{item.limitId}
    and wid = #{item.wid}
    and is_deleted = 0)

/*UserGoodsLimitDao.queryUserGoodsLimitList*/

mapper.update:
UPDATE
    user_goods_limit
SET
    buy_num = buy_num+#{buyNum},
    update_time = NOW()
WHERE
     pid = #{pid}
AND store_id = #{storeId}
AND wid = #{wid}
AND limit_id = #{limitId}
AND goods_id = #{goodsId}
/*UserGoodsLimitDao.update*/

mapper.select:
select
usergoodslimit.pid,
usergoodslimit.store_id,
usergoodslimit.limit_id,
usergoodslimit.goods_id,
usergoodslimit.buy_num
from
user_goods_limit as usergoodslimit,
limit_info as limitinfo
where
limitinfo.limit_id=usergoodslimit.limit_id
AND (

    (
    limitinfo.pid = #{item.pid}
    and limitinfo.biz_id = #{item.bizId}
    and limitinfo.biz_type = #{item.bizType}
    and wid = #{item.wid}
    and usergoodslimit.is_deleted = 0
    and limitinfo.is_deleted = 0
    )

)
/*UserGoodsLimitDao.queryUserOrderGoodsLimitList*/

select

from
user_goods_limit
where
pid = #{pid}
and store_id = #{storeId}
and wid = #{wid}
and limit_id = #{limitId}
and goods_id = #{goodsId}
and is_deleted = 0
/*UserGoodsLimitDao.getUserGoodsLimitEntity*/

mapper.update:
UPDATE
    user_goods_limit
SET
    buy_num = buy_num - #{buyNum},
    update_time = NOW()
WHERE
    pid = #{pid}
    AND store_id = #{storeId}
    AND wid = #{wid}
    AND limit_id = #{limitId}
    AND goods_id = #{goodsId}
    AND buy_num >= #{buyNum}
/*UserGoodsLimitDao.deductUserGoodsLimit*/

UPDATE
user_goods_limit
SET
wid =-1 * wid,
update_time = NOW()
WHERE
pid = #{pid}
AND limit_id = #{limitId}
AND is_deleted = 0
/*UserGoodsLimitDao.deleteDiscountUserLimitInfo*/
