mapper.update:
UPDATE
    user_limit
SET
    buy_num = buy_num + #{buyNum},
    update_time = NOW()
where
    pid = #{pid}
    AND store_id = #{storeId}
    AND wid = #{wid}
    AND limit_id = #{limitId}
/*UserLimitDao.update*/

mapper.select:
select

from
user_limit
where
pid = #{pid}
and store_id = #{storeId}
and wid = #{wid}
and limit_id = #{limitId}
and is_deleted = 0
/*UserLimitDao.getUserLimitEntity*/

select userlimit.pid pid, userlimit.store_id store_id, userlimit.limit_id limit_id, userlimit.biz_id biz_id, userlimit.biz_type biz_type, userlimit.buy_num buy_num from user_limit as userlimit, limit_info as limitinfo where limitinfo.limit_id=userlimit.limit_id AND
userlimit.pid = #{item.pid} and userlimit.store_id = #{item.storeId} and userlimit.wid =#{item.wid} and userlimit.biz_id =#{item.bizId} and userlimit.biz_type=#{item.bizType} and userlimit.is_deleted = 0 and limitinfo.is_deleted = 0
/*UserLimitDao.queryUserLimitInfoList*/

select

from
user_limit
where

    pid = #{item.pid}
    and store_id = #{item.storeId}
    and wid = #{item.wid}
    and limit_id = #{item.limitId}
    and is_deleted = 0

/*UserLimitDao.queryUserLimitEntityList*/

mapper.update:
UPDATE
user_limit
SET
buy_num = buy_num - #{buyNum},
update_time = NOW()
WHERE
pid = #{pid}
AND store_id = #{storeId}
AND wid = #{wid}
AND limit_id = #{limitId}
AND buy_num >= #{buyNum}
/*UserLimitDao.deductUserLimit*/
